import java.io.*;
import java.net.HttpURLConnection;
import java.net.URL;
import org.apache.commons.io.FileUtils;

public class Main {
    public static void main(String[] args) throws Exception {
        String apiUrl = "https://api.example.com/api/public/v1/deecho";
        String apiKey = "<API_KEY>";

        // Option 1: URL
        String jsonInput = "{\"audio_url\": \"https://example.com/audio.m4a\"}";
        HttpURLConnection con = (HttpURLConnection) new URL(apiUrl).openConnection();
        con.setRequestMethod("POST");
        con.setRequestProperty("Authorization", apiKey);
        con.setRequestProperty("Content-Type", "application/json");
        con.setDoOutput(true);
        try(OutputStream os = con.getOutputStream()) {
            byte[] input = jsonInput.getBytes("utf-8");
            os.write(input, 0, input.length);
        }
        try(BufferedReader br = new BufferedReader(new InputStreamReader(con.getInputStream(), "utf-8"))) {
            StringBuilder response = new StringBuilder();
            String responseLine;
            while ((responseLine = br.readLine()) != null) {
                response.append(responseLine.trim());
            }
            System.out.println(response.toString());
        }

        // Option 2: File Upload
        String boundary = Long.toHexString(System.currentTimeMillis());
        con = (HttpURLConnection) new URL(apiUrl).openConnection();
        con.setRequestMethod("POST");
        con.setRequestProperty("Authorization", apiKey);
        con.setRequestProperty("Content-Type", "multipart/form-data; boundary=" + boundary);
        con.setDoOutput(true);
        try (OutputStream out = con.getOutputStream();
             PrintWriter writer = new PrintWriter(new OutputStreamWriter(out, "UTF-8"), true)) {
            File file = new File("audio.m4a");
            writer.append("--" + boundary).append("\r\n");
            writer.append("Content-Disposition: form-data; name=\"audio_file\"; filename=\"" + file.getName() + "\"").append("\r\n");
            writer.append("Content-Type: audio/mp4").append("\r\n");
            writer.append("\r\n").flush();
            FileUtils.copyFile(file, out);
            out.flush();
            writer.append("\r\n").flush();
            writer.append("--" + boundary + "--").append("\r\n").flush();
        }
        try(BufferedReader br = new BufferedReader(new InputStreamReader(con.getInputStream(), "utf-8"))) {
            StringBuilder response = new StringBuilder();
            String responseLine;
            while ((responseLine = br.readLine()) != null) {
                response.append(responseLine.trim());
            }
            System.out.println(response.toString());
        }
    }
}

